import path from 'path';
/**
 * find template file path
 * @param  {String}  
 * @return {Class}
 */
export default class extends think.middleware.base {
  /**
   * get path prefix
   * @return {String} []
   */
  getPathPrefix(module){
    let pathPrefix, http = this.http, prefix = '';
    let {root_path} = this.options;
    let theme = http.theme();
    let lang = http._langAsViewPath && http.lang();

    module = module || http.module;

    //support locale
    if(lang){
      prefix += think.sep + lang;
    }
    //support theme
    if(theme){
      prefix += think.sep + theme;
    }

    //view root path is defined
    if(root_path){
      pathPrefix = path.normalize(root_path);
      if(think.mode === think.mode_module){
        pathPrefix += prefix + think.sep + module;
      }
    }else{
      pathPrefix = think.getPath(module, think.dirname.view, prefix);
    }

    return pathPrefix;
  }
  /**
   * run
   * @param  {String} templateFile [template filepath]
   * @return {}              []
   */
  run(options){
    if(!think.isObject(options)){
      options = think.extend({
        templateFile: options
      }, this.config('view'));
    }
    this.options = options;

    let templateFile = options.templateFile;
    //is absolute file path
    if(templateFile && path.isAbsolute(templateFile)){
      return templateFile;
    }
    let http = this.http;
    let {file_depr, file_ext} = options;
    let pathPrefix = this.getPathPrefix();
    let controller = http.controller.replace(/\//g, think.sep);

    //if file_depr is /, replace to think.sep, avoid error on windows
    if(file_depr === '/'){
      file_depr = think.sep;
    }

    // this.display()
    if (!templateFile) {
      return pathPrefix + think.sep + controller + file_depr + http.action + file_ext;
    }
    //replace : to /
    templateFile = templateFile.replace(/\:/g, '/');

    // this.display('detail')
    // this.display('index/detail')
    // this.display('admin/index/detail')
    // this.display('admin/index/detail.html')
    let paths = templateFile.split('/');
    let length = paths.length;
    let action = paths[length - 1];

    let module;
    if(length === 2){
      controller = paths[0];
    }else if(length > 2){
      let index = think.module.indexOf(paths[0]) > -1 ? 1 : 0;
      if(index){
        module = paths[0];
      }
      let newController = paths.slice(index, length - 1).join(think.sep);
      if(newController){
        controller = newController;
      }
    }

    if (module && module !== http.module) {
      pathPrefix = this.getPathPrefix(module);
    }

    templateFile = pathPrefix + think.sep + controller + file_depr + action;
    if (action.indexOf('.') === -1) {
      templateFile += file_ext;
    }
    return templateFile;
  }
}

'use strict';

import fs from 'fs';
import mime from 'mime';

/**
 * output resource
 * @type {}
 */
export default class extends think.middleware.base {
  /**
   * run
   * @return {} []
   */
  run(file){
    //not resource
    if(file === false){
      return;
    }
    let http = this.http;
    //is resource but not exist
    if(file === true){
      http.status(404);
      http.end();
      return think.prevent();
    }
    //flag request is resource
    http._isResource = true;

    let contentType = mime.lookup(file);
    http.type(contentType, false);

    let range = http.header('range');
    if(!range){
      return this.outputNormal(file);
    }
    return this.outputRange(file, range);
  }
  /**
   * output normal file
   * @param  {String} file []
   * @return {Promise}      []
   */
  outputNormal(file){
    let http = this.http;
    let stream = fs.createReadStream(file);
    stream.pipe(http.res);
    stream.on('end', () => {
      http.end();
    });
    stream.on('error', () => {
      http.end();
    });
    return think.prevent();
  }
  /**
   * output range file
   * @param  {String} file  []
   * @param  {String} range []
   * @return {Promise}       []
   */
  outputRange(file, range){
    //request has range header
    let size = fs.statSync(file).size;
    let match = range.match(/bytes=(\d+)\-(\d*)/);
    let slice = 1 * 1024 * 1024;
    let from = parseInt(match[1]) || 0;
    let to = parseInt(match[2]) || 0;
    if(!to){
      to = from + slice - 1;
    }
    to = Math.min(to, size - 1);

    let http = this.http;
    http.status(206);
    http.header('Accept-Ranges', 'bytes');
    http.header('Content-Range', `bytes ${from}-${to}/${size}`);

    let fd = fs.openSync(file, 'r');
    let buffer = new Buffer(to - from + 1);
    fs.readSync(fd, buffer, 0, to - from, from);
    fs.closeSync(fd);
    http.end(buffer);

    return think.prevent();
  }
}

'use strict';

import os from 'os';
import multiparty from 'multiparty';


const MULTIPARTY_REG = /^multipart\/(form-data|related);\s*boundary=(?:"([^"]+)"|([^;]+))$/i;

/**
 * parse form payload
 * @type {}
 */
export default class extends think.middleware.base {
  /**
   * run
   * @return {} []
   */
  run(){
    let http = this.http;
    if(!http.req.readable){
      return;
    }

    //file upload by form or FormData
    //can not use http.type method
    if (!MULTIPARTY_REG.test(http.headers['content-type'])) {
      return;
    }

    //make upload file path
    let uploadDir = think.config('post.file_upload_path');
    if(!uploadDir){
      uploadDir = os.tmpdir() + think.sep + 'thinkjs' + think.sep + 'upload';
    }
    think.mkdir(uploadDir);

    return this.getFormData(uploadDir);
  }
  /**
   * get form data
   * @return {Promise} []
   */
  getFormData(uploadDir){
    let http = this.http;
    let deferred = think.defer();
    let postConfig = think.config('post');
    let form = new multiparty.Form({
      maxFieldsSize: postConfig.max_fields_size,
      maxFields: postConfig.max_fields,
      maxFilesSize: postConfig.max_file_size,
      uploadDir: uploadDir
    });
    //support for file with multiple="multiple"
    let files = http._file;
    form.on('file', (name, value) => {
      if (name in files) {
        if (!think.isArray(files[name])) {
          files[name] = [files[name]];
        }
        files[name].push(value);
      }else{
        files[name] = value;
      }
    });
    form.on('field', (name, value) => {
      http._post[name] = value;
    });
    form.on('close', () => {
      deferred.resolve(null);
    });
    form.on('error', err => {
      http.req.resume();
      http.res.statusCode = 400;
      http.end();
      //log error
      if(http.config('post.log_error')){
        think.log(err);
      }
    });
    form.parse(http.req);
    return deferred.promise;
  }
}

'use strict';

import querystring from 'querystring';

/**
 * parse json payload
 * @type {}
 */
export default class extends think.middleware.base {
  /**
   * run
   * @return {} []
   */
  run(){
    let http = this.http;
    if(!http.req.readable){
      return;
    }

    let types = http.config('post.json_content_type');
    if (types.indexOf(http.type()) === -1) {
      return;
    }
    return http.getPayload().then(payload => {
      let data;
      try{
        data = JSON.parse(payload);
      }catch(e){
        //log error
        if(http.config('post.log_error')){
          think.log(new Error('JSON.parse error, payload is not a valid JSON data'));
        }
        //if using json parse error, then use querystring parse.
        //sometimes http header has json content-type, but payload data is querystring data
        data = querystring.parse(payload);
      }
      if(!think.isEmpty(data)){
        http._post = think.extend(http._post, data);
      }
      return null;
    });
  }
}

